mapper.select:
SELECT * FROM
	(SELECT rownum r_num, a.* FROM
		(SELECT * FROM normal_reply 
		WHERE nb_num = #{nb_num}
		ORDER BY nreply_group DESC,nreply_cdate ASC)a)
WHERE r_num BETWEEN  #{begin} AND #{end}

mapper.insert:
		INSERT INTO normal_reply(nreply_num,nb_num,	m_id,nreply_to,nreply_group, nreply_content,
            						nreply_cdate,nreply_ip,nreply_status)
		VALUES(normal_reply_seq.NEXTVAL, #{nb_num},	#{m_id},#{nreply_to}

					, #{nreply_group}


					, normal_reply_group_seq.NEXTVAL

			, #{nreply_content},sysdate,#{nreply_ip},0)

mapper.update:
UPDATE normal_board set 

nb_reply_count = nb_reply_count + 1

WHERE nb_num = #{nb_num}
